        ///                 "type": "commit_file",
        ///                 "links": {
        ///                     "self": {
        ///                         "href": "https://api.bitbucket.org/2.0/repositories/bitbucket/geordi/src/e1749643d655d7c7014001a6c0f58abaf42ad850/setup.py"
        ///                     }
