        ///         }
        ///     }
        /// 
        ///     --===============1438169132528273974==
        ///     Content-Type: text/plain; charset="us-ascii"
        ///     MIME-Version: 1.0
        ///     Content-Transfer-Encoding: 7bit
        ///     Content-ID: "foo.txt"
        ///     Content-Disposition: attachment; filename="foo.txt"
        /// 
