        ///               "line": 2,
        ///               "segments": []
        ///             },
        ///             {
        ///               "line": 3,
        ///               "segments": [
        ///                 {
        ///                   "text": "def "
        ///                 },
        ///                 {
        ///                   "text": "foo",
        ///                   "match": true
        ///                 },
        ///                 {
        ///                   "text": "():"
        ///                 }
        ///               ]
        ///             },
        ///             {
        ///               "line": 4,
        ///               "segments": [
        ///                 {
        ///                   "text": "    print(\"snek\")"
        ///                 }
        ///               ]
        ///             },
        ///             {
        ///               "line": 5,
        ///               "segments": []
        ///             }
        ///           ]
        ///         }
        ///       ],
        ///       "path_matches": [
        ///         {
        ///           "text": "src/"
        ///         },
        ///         {
        ///           "text": "foo",
        ///           "match": true
        ///         },
        ///         {
        ///           "text": ".py"
        ///         }
        ///       ],
        ///       "file": {
        ///         "path": "src/foo.py",
        ///         "type": "commit_file",
        ///         "links": {
        ///           "self": {
        ///             "href": "https://api.bitbucket.org/2.0/repositories/my-workspace/demo/src/ad6964b5fe2880dbd9ddcad1c89000f1dbcbc24b/src/foo.py"
