        ///               "line": 2,
        ///               "segments": []
        ///             },
        ///             {
        ///               "line": 3,
        ///               "segments": [
        ///                 {
        ///                   "text": "def "
        ///                 },
        ///                 {
        ///                   "text": "foo",
        ///                   "match": true
        ///                 },
        ///                 {
        ///                   "text": "():"
        ///                 }
        ///               ]
        ///             },
        ///             {
        ///               "line": 4,
        ///               "segments": [
        ///                 {
        ///                   "text": "    print(\"snek\")"
        ///                 }
        ///               ]
        ///             },
        ///             {
        ///               "line": 5,
        ///               "segments": []
        ///             }
        ///           ]
        ///         }
        ///       ],
        ///       "path_matches": [
        ///         {
        ///           "text": "src/"
        ///         },
        ///         {
        ///           "text": "foo",
        ///           "match": true
        ///         },
        ///         {
        ///           "text": ".py"
        ///         }
